def load_and_prep_image(filename, img_shape=224):
  """
Dosya adından bir görüntüyü okur, tensöre dönüştürür ve onu (img_shape, img_shape, colour_channel) olarak yeniden şekillendirir.
  """
  # Hedef dosyada okuma (bir resim)

  img = tf.io.read_file(filename)

  # Okunan dosyanın kodunu bir tensöre dönüştürülmesi ve 3 renk kanalının sağlanması
   # (modelimiz 3 renk kanallı görüntüler üzerinde eğitilmiştir ve bazen görüntülerde 4 renk kanalı bulunur)

  img = tf.image.decode_image(img, channels=3)

  # Resmi yeniden boyutlandırılması (modelimizin eğitildiği boyutla aynı)

  img = tf.image.resize(img, size = [img_shape, img_shape])

  # Fotoğrafın rescale edilmesi (0 ve 1 arasına)
  img = img/255.
  return img

